//////////////////////////////////////////////////////////////////////
// Purpose: Product Page Component
// Author: Siddharth Karanchery
//////////////////////////////////////////////////////////////////////

import React, { useEffect, useState } from "react";

import emailjs from "emailjs-com";

import ImageCarousel from "../ImageCarousel/ImageCarousel";

import {
  FaShareAlt,
  FaTwitter,
  FaFacebook,
  FaLinkedin,
  FaHeart,
} from "react-icons/fa";

import axios from "axios";

import { Container, Row, Col, Accordion, Button, Modal } from "react-bootstrap";
import {
  Card,
  CardBody,
  CardFooter,
  Form,
  Input,
  InputGroup,
  Textarea,
  InputGroupAddon,
  InputGroupText,
} from "reactstrap";

import { useParams, useNavigate } from "react-router-dom";

// import productDetails from "../../Data/ProductDetails.json";

import "./ProductPage.css";
import ShareModal from "./ShareModal";

function ProductPage(props) {
  const [showModal, setShowModal] = useState(false);
  const [productDetails, setProductDetails] = useState({});

  const { product } = useParams();

  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(
        "http://localhost:34365/api/ProductEmpty/GetProductDetailsByProductName?ProductName=" +
          product
      )
      .then((result) => {
        console.log(result);
        setProductDetails(result.data[0]);
      });
  }, []);

  return (
    <div className="ProductPage">
      {showModal ? (
        <ShareModal setShowModal={setShowModal} showModal={showModal} />
      ) : null}
      <div className="ProductPage__ele">
        <ImageCarousel images={productDetails.pictures} />
      </div>
      <div className="ProductPage__ele" id="ele__details">
        <Container class="d-flex align-items-center">
          <Row>
            <h2>{productDetails.productName}</h2>
          </Row>
          <Row>
            <h4>Description</h4>
            <p>{productDetails.productDescription}</p>
          </Row>
          <Row>
            <Col>
              <h4>Price</h4>
            </Col>
            <Col>
              <h4>"Rs. " {productDetails.productPrice}</h4>
            </Col>
          </Row>
          <Row>
            <Col>
              <p>Shipping Charges</p>
            </Col>
            <Col>
              <p>
                {productDetails.currency} {productDetails.shipping}
              </p>
            </Col>
          </Row>
          <Row>
            <Col>
              <Button variant="success">Add to Cart</Button>
            </Col>
            <Col>
              <Button
                variant="warning"
                onClick={() => {
                  navigate("/Cart");
                }}
              >
                Go to Cart
              </Button>
            </Col>
            <Col>
              <Button variant="secondary">Enquiry</Button>
            </Col>
          </Row>
          <Row className="ProductPage__ele__social">
            <Col>
              <FaHeart />
            </Col>
            <Col>
              <FaShareAlt
                onClick={() => {
                  setShowModal(true);
                }}
              />
            </Col>
            <Col>
              <FaTwitter
                className="Header__header__nav__icons"
                size={20}
                onClick={() => {
                  window.open("https://www.twitter.com", "_blank");
                }}
              />
            </Col>
            <Col>
              <FaFacebook
                className="Header__header__nav__icons"
                size={20}
                onClick={() => {
                  window.open("https://www.facebook.com", "_blank");
                }}
              />
            </Col>
            <Col>
              <FaLinkedin
                className="Header__header__nav__icons"
                size={20}
                onClick={() => {
                  window.open("https://www.linkedin.com", "_blank");
                }}
              />
            </Col>
          </Row>
          <Row>
            <h4>Product Details</h4>
            <Accordion defaultActiveKey="" flush>
              <Accordion.Item eventKey="0">
                <Accordion.Header>Brand</Accordion.Header>
                <Accordion.Body>{productDetails.brand}</Accordion.Body>
              </Accordion.Item>
              <Accordion.Item eventKey="1">
                <Accordion.Header>Type</Accordion.Header>
                <Accordion.Body>{productDetails.type}</Accordion.Body>
              </Accordion.Item>
              <Accordion.Item eventKey="2">
                <Accordion.Header>Origin</Accordion.Header>
                <Accordion.Body>{productDetails.origin}</Accordion.Body>
              </Accordion.Item>
            </Accordion>
          </Row>
        </Container>
      </div>
    </div>
  );
}

export default ProductPage;
