import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./AccountDetails.css";

function AccountDetails() {
  const [account, setAccount] = useState({
    FirstName: "",
    LastName: "",
    EmailId: "",
    Password: "",
    ConfirmPassword: "",
    Address: "",
    MobileNo: "",
  });

  const { emailId } = useParams();

  useEffect(() => {
    console.log(emailId);
    axios
      .get(
        `http://localhost:34365/api/BuyerAccountDetailsByEmail/GetBuyerDetailsByEmail?Email=` +
          emailId
      )
      .then((result) => {
        console.log(result);
        // let tempdetails = {
        //   FirstName: result.data.firstName,
        //   LastName: result.data.lastName,
        //   EmailId: result.data.emailId,
        //   Password: result.data.password,
        //   ConfirmPassword: "",
        //   Address: result.data.buyerAddress,
        //   MobileNo: result.data.mobileNo,
        // };
        // console.log(tempdetails);
        // setAccount(tempdetails);
      });
  }, []);

  const handleChange = (e) => {
    e.preventDefault();
    e.persist();
    setAccount({ ...account, [e.target.name]: e.target.value });
  };
  const onSubmit = (e) => {
    // debugger;
    e.preventDefault();
    console.log(account);
    const obj = {
      // id: this.props.match.params.id,
      FirstName: account.FirstName,
      LastName: account.LastName,
      EmailId: account.EmailId,
      Address: account.Address,
      MobileNo: account.MobileNo,
    };
    // setAccount(obj);
    // console.log(obj);
    // axios
    //   .post("http://localhost:16711/api/Student/AddotrUpdatestudent", obj)
    //   .then((res) => console.log(res.data));
    // // debugger;
    // this.props.history.push("/Studentlist");
  };

  const UpdateDetailsClick = () => {};

  return (
    <div className="AccountDetails">
      <h4
        style={{ backgroundColor: "var(--c2d)", width: "100%", color: "white" }}
      >
        Account Details
      </h4>
      <form onSubmit={onSubmit}>
        <div className="row">
          <div className="col-md-6">
            <input
              className="form-control"
              type="text"
              name="FirstName"
              value={account.FirstName}
              onChange={handleChange}
              required
              placeholder="First Name"
            />
          </div>
          <div className="col-md-6">
            <input
              className="form-control"
              type="text"
              value={account.LastName}
              name="LastName"
              onChange={handleChange}
              required
              placeholder="Last Name"
            />
          </div>
          <div className="col-md-6">
            <input
              className="form-control"
              type="text"
              value={account.EmailId}
              name="EmailId"
              onChange={handleChange}
              required
              placeholder="EmailId"
            />
          </div>
          <div className="col-md-6">
            <input
              className="form-control"
              type="text"
              value={account.MobileNo}
              name="MobileNo"
              onChange={handleChange}
              required
              placeholder="MobileNo"
            />
          </div>
          <div className="col-md-12">
            <input
              className="form-control"
              type="text"
              value={account.Address}
              name="Address"
              onChange={handleChange}
              placeholder="Address"
            />
          </div>

          <div className="col-md-12">
            <button className="btn__account" onClick={UpdateDetailsClick}>
              Update Account
            </button>
            <br />
            <br />
          </div>
        </div>
      </form>
      <h4
        style={{ backgroundColor: "var(--c2d)", width: "100%", color: "white" }}
      >
        Password change
      </h4>
      <form>
        <div className="row">
          <div className="col-md-12">
            <input
              className="form-control"
              type="password"
              onChange={handleChange}
              placeholder="Current Password"
            />
          </div>
          <div className="col-md-6">
            <input
              className="form-control"
              type="password"
              onChange={handleChange}
              placeholder="New Password"
            />
          </div>
          <div className="col-md-6">
            <input
              className="form-control"
              type="password"
              onChange={handleChange}
              placeholder="Confirm Password"
            />
          </div>
          <div className="col-md-12">
            <button className="btn__account">Save Changes</button>
          </div>
        </div>
      </form>
    </div>
  );
}
export default AccountDetails;
