//////////////////////////////////////////////////////////////////////
// Purpose: Checkout Page Component
// Author: Siddharth Karanchery
//////////////////////////////////////////////////////////////////////

import React, { useEffect, useState } from "react";

import { Container, Row, Col, Accordion, Button, Image } from "react-bootstrap";
import { Input } from "reactstrap";

import { useNavigate } from "react-router-dom";

import cartDetails from "../../Data/CartDetails.json";

import "./Checkout.css";

function Checkout(props) {
  const navigate = useNavigate();
  let subtotal = 0;
  let deliveryCharges = 0;

  cartDetails.forEach((item) => {
    subtotal += item.quantity * item.price;
    deliveryCharges += item.shipping;
  });
  return (
    <div className="Checkout">
      <div className="Checkout__left">
        <h4>Card Details</h4>
        <Container>
          <Row>
            <Input />
          </Row>
          <Row>
            <Input />
          </Row>
          <Row>
            <Col>
              <Input />
            </Col>
            <Col>
              <Input />
            </Col>
          </Row>
        </Container>
      </div>
      <div className="Checkout__right">
        <h4>Total</h4>
        <Container className="d-flex flex-column ">
          <Row>
            <Col>Items: </Col>
            <Col>{cartDetails.length}</Col>
          </Row>
          <Row>
            <Col>SubTotal: </Col>
            <Col>{subtotal}</Col>
          </Row>
          <Row>
            <Col>Delivery Charges: </Col>
            <Col>{deliveryCharges}</Col>
          </Row>
          <Row className="Cart__right__total align-self-center mt-2">
            <Col>Total: </Col>
            <Col>{subtotal + deliveryCharges}</Col>
          </Row>
          <Row className="w-50 align-self-center my-3">
            <Button
              variant="warning"
              onClick={() => {
                navigate("/Checkout");
              }}
            >
              Buy
            </Button>
          </Row>
          <Row className="w-75 align-self-center ">
            <Button
              variant="light"
              onClick={() => {
                navigate("/Cart");
              }}
            >
              Go back to Cart
            </Button>
          </Row>
        </Container>
      </div>
    </div>
  );
}

export default Checkout;
