//////////////////////////////////////////////////////////////////////
// Purpose: Product Page Component
// Author: Siddharth Karanchery
//////////////////////////////////////////////////////////////////////

import React, { useEffect, useState } from "react";

import ImageCarousel from "../ImageCarousel/ImageCarousel";

import { FaShareAlt, FaTwitter, FaFacebook, FaLinkedin } from "react-icons/fa";

import { Container, Row, Col, Card, Button } from "react-bootstrap";

import { useParams } from "react-router-dom";

import productDetails from "../../Data/ProductDetails.json";

import "./ProductPage.css";

function ProductPage(props) {
  const { product } = useParams();
  return (
    <div className="ProductPage">
      <div className="ProductPage__ele">
        <ImageCarousel images={productDetails.pictures} />
      </div>
      <div className="ProductPage__ele" id="ele__details">
        <Container class="d-flex align-items-center">
          <Row>
            <h2>{product}</h2>
          </Row>
          <Row>
            <h4>Description</h4>
            <p>{productDetails.description}</p>
          </Row>
          <Row>
            <Col>
              <h4>Price</h4>
            </Col>
            <Col>
              <p>
                {productDetails.currency} {productDetails.price}
              </p>
            </Col>
          </Row>
          <Row>
            <Col>
              <p>Shipping Charges</p>
            </Col>
            <Col>
              <p>
                {productDetails.currency} {productDetails.shipping}
              </p>
            </Col>
          </Row>
          <Row>
            <Col>
              <Button variant="warning">Add to Cart</Button>
            </Col>
            <Col>
              <button>Make an Enquiry</button>
            </Col>
          </Row>
          <Row className="ProductPage__ele__social">
            <Col>
              <FaShareAlt />
            </Col>
            <Col>
              <FaTwitter
                className="Header__header__nav__icons"
                size={20}
                onClick={() => {
                  window.open("https://www.twitter.com", "_blank");
                }}
              />
            </Col>
            <Col>
              <FaFacebook
                className="Header__header__nav__icons"
                size={20}
                onClick={() => {
                  window.open("https://www.facebook.com", "_blank");
                }}
              />
            </Col>
            <Col>
              <FaLinkedin
                className="Header__header__nav__icons"
                size={20}
                onClick={() => {
                  window.open("https://www.linkedin.com", "_blank");
                }}
              />
            </Col>
          </Row>
        </Container>
      </div>
    </div>
  );
}

export default ProductPage;
