//////////////////////////////////////////////////////////////////////
// Purpose: Product Page Component
// Author: Siddharth Karanchery
//////////////////////////////////////////////////////////////////////

import React, { useEffect, useState } from "react";

import emailjs from "emailjs-com";

import ImageCarousel from "../ImageCarousel/ImageCarousel";

import {
  FaShareAlt,
  FaTwitter,
  FaFacebook,
  FaLinkedin,
  FaHeart,
} from "react-icons/fa";

import axios from "axios";

import { Container, Row, Col, Accordion, Button, Modal } from "react-bootstrap";
import {
  Card,
  CardBody,
  CardFooter,
  Form,
  Input,
  InputGroup,
  Textarea,
  InputGroupAddon,
  InputGroupText,
} from "reactstrap";

import { useParams, useNavigate } from "react-router-dom";

import { useSelector, useDispatch } from "react-redux";

// import productDetails from "../../Data/ProductDetails.json";

import "./ProductPage.css";
import ShareModal from "./ShareModal";

function ProductPage(props) {
  const [showModal, setShowModal] = useState(false);
  const [productDetails, setProductDetails] = useState({});

  const { product } = useParams();

  const navigate = useNavigate();
  const dispatch = useDispatch();

  const userDetails = useSelector((state) => state.userDetails);

  useEffect(() => {
    axios
      .get(
        "http://localhost:34365/api/ProductEmpty/GetProductDetailsByProductName?ProductName=" +
          product
      )
      .then((result) => {
        console.log(result);
        setProductDetails(result.data[0]);
      });
  }, []);

  const addToCartHandler = () => {
    let itemDetails = {
      buyerId: userDetails.Id,
      productId: productDetails.productId,
      productName: productDetails.productName,
      productPrice: productDetails.productPrice,
      productQuantity: 1,
      productImage: "string",
    };
    dispatch({ type: "addUserDetails", userDetails: tempdetails });
  };

  return (
    <div className="ProductPage">
      {showModal ? (
        <ShareModal setShowModal={setShowModal} showModal={showModal} />
      ) : null}
      <div className="ProductPage__ele">
        <ImageCarousel images={productDetails.pictures} />
      </div>
      <div className="ProductPage__ele" id="ele__details">
        <Container class="d-flex align-items-center">
          <Row>
            <h2>{productDetails.productName}</h2>
          </Row>
          <Row>
            <h4>Description</h4>
            <p>{productDetails.productDescription}</p>
          </Row>
          <Row>
            <Col>
              <h4>Price</h4>
            </Col>
            <Col>
              <h4>Rs. {productDetails.productPrice}</h4>
            </Col>
          </Row>
          <Row>
            <Col>
              <p>Shipping Charges</p>
            </Col>
            <Col>
              <p>Rs. {productDetails.deliveryCharge}</p>
            </Col>
          </Row>
          <Row>
            <Col>
              <Button variant="success" onClick={addToCartHandler}>
                Add to Cart
              </Button>
            </Col>
            <Col>
              <Button
                variant="warning"
                onClick={() => {
                  navigate("/Cart");
                }}
              >
                Go to Cart
              </Button>
            </Col>
            <Col>
              <Button variant="secondary">Enquiry</Button>
            </Col>
          </Row>
          <Row className="ProductPage__ele__social">
            <Col>
              <FaHeart />
            </Col>
            <Col>
              <FaShareAlt
                onClick={() => {
                  setShowModal(true);
                }}
              />
            </Col>
            <Col>
              <FaTwitter
                className="Header__header__nav__icons"
                size={20}
                onClick={() => {
                  window.open("https://www.twitter.com", "_blank");
                }}
              />
            </Col>
            <Col>
              <FaFacebook
                className="Header__header__nav__icons"
                size={20}
                onClick={() => {
                  window.open("https://www.facebook.com", "_blank");
                }}
              />
            </Col>
            <Col>
              <FaLinkedin
                className="Header__header__nav__icons"
                size={20}
                onClick={() => {
                  window.open("https://www.linkedin.com", "_blank");
                }}
              />
            </Col>
          </Row>
          <Row>
            <h4>Product Details</h4>
            <Accordion defaultActiveKey="" flush>
              <Accordion.Item eventKey="0">
                <Accordion.Header>Brand</Accordion.Header>
                <Accordion.Body>
                  {productDetails.productBrandName}
                </Accordion.Body>
              </Accordion.Item>
              <Accordion.Item eventKey="1">
                <Accordion.Header>Type</Accordion.Header>
                <Accordion.Body>{productDetails.productSubType}</Accordion.Body>
              </Accordion.Item>
              <Accordion.Item eventKey="2">
                <Accordion.Header>Origin</Accordion.Header>
                <Accordion.Body>
                  {productDetails.productionCountryOrigin}
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
          </Row>
        </Container>
      </div>
    </div>
  );
}

export default ProductPage;
