//////////////////////////////////////////////////////////////////////
// Purpose: Cart Component
// Author: Siddharth Karanchery
//////////////////////////////////////////////////////////////////////

import React, { useEffect, useState } from "react";

import ImageCarousel from "../ImageCarousel/ImageCarousel";

import { FaShareAlt, FaTwitter, FaFacebook, FaLinkedin } from "react-icons/fa";

import { Container, Row, Col, Accordion, Button } from "react-bootstrap";

import { useParams } from "react-router-dom";

import cartDetails from "../../Data/CartDetails.json";

import "./Cart.css";
import CartCard from "./CartCard";

// - Product thumbnail
// - Brief info
// - Color, size selected
// -  Add/Update carts in real time
// -  Shipping options
// - Order sub total, calculated value
// - Continue shopping button (will take back user to the products section)
// - Checkout button (will move forward user to the checkout screen)

function Cart(props) {
  let subtotal = 0;
  let deliveryCharges = 0;

  cartDetails.forEach((item) => {
    subtotal += item.quantity * item.price;
    deliveryCharges += item.shipping;
  });

  return (
    <div className="Cart">
      <div className="Cart__left">
        {cartDetails.length === 0 ? (
          "Cart is Empty"
        ) : (
          <>
            <h4>Cart</h4>
            <Container className="d-flex flex-column align-items-center justify-content-center">
              {cartDetails.map((item) => {
                return <CartCard item={item} />;
              })}
            </Container>
          </>
        )}
      </div>
      <div className="Cart__right" hidden={cartDetails.length === 0}>
        <h4>Total</h4>
        <Container className="d-flex flex-column ">
          <Row>
            <Col>Items: </Col>
            <Col>{cartDetails.length}</Col>
          </Row>
          <Row>
            <Col>SubTotal: </Col>
            <Col>{subtotal}</Col>
          </Row>
          <Row>
            <Col>Delivery Charges: </Col>
            <Col>{deliveryCharges}</Col>
          </Row>
          <Row className="Cart__right__total align-self-center mt-2">
            <Col>Total: </Col>
            <Col>{subtotal + deliveryCharges}</Col>
          </Row>
          <Row className="w-50 align-self-center my-3">
            <Button variant="warning">Checkout</Button>
          </Row>
        </Container>
      </div>
    </div>
  );
}

export default Cart;
