//////////////////////////////////////////////////////////////////////
// Purpose: Checkout Page Component
// Author: Siddharth Karanchery
//////////////////////////////////////////////////////////////////////

import React, { useEffect, useState } from "react";

import {
  Container,
  Row,
  Col,
  Button,
  Card,
  DropdownButton,
  Dropdown,
} from "react-bootstrap";
import {
  CardBody,
  Input,
  ButtonDropdown,
  DropdownMenu,
  DropdownItem,
  DropdownToggle,
  InputGroup,
} from "reactstrap";

import { useNavigate } from "react-router-dom";

import cartDetails from "../../Data/CartDetails.json";

import "./Checkout.css";

function Checkout(props) {
  const [chName, setChname] = useState("");
  const [cardNum, setCardnum] = useState("");
  const [cardType, setCardtype] = useState("");
  const [bank, setBank] = useState("");
  const [expiry, setExpiry] = useState("");
  const [cvv, setCVV] = useState("");

  const navigate = useNavigate();

  const changeHandler = (e) => {
    console.log(e);
    switch (e.target.name) {
      case "chName":
        setChname(e.target.value);
        break;
      case "cardNum":
        setCardnum(e.target.value);
        break;
      case "cardType":
        setCardtype(e.target.innerText);
        break;
      default:
        break;
    }
    console.log("chName: ", chName);
    console.log("cardNum: ", cardNum);
    console.log("cardType: ", cardType);
  };

  const selectHandler = (e) => {
    console.log(e.target.name);
  };

  let subtotal = 0;
  let deliveryCharges = 0;

  cartDetails.forEach((item) => {
    subtotal += item.quantity * item.price;
    deliveryCharges += item.shipping;
  });
  return (
    <div className="Checkout">
      <div className="Checkout__left">
        <h4>Card Details</h4>
        <Container className="w-75 h-100">
          <Card className="p-3">
            <CardBody>
              <Row className="my-2">
                <Input
                  placeholder="Cardholder Name"
                  value={chName}
                  name="chName"
                  onChange={changeHandler}
                />
              </Row>
              <Row className="my-2">
                <Input
                  placeholder="Card Number"
                  value={cardNum}
                  name="cardNum"
                  onChange={changeHandler}
                />
              </Row>
              <Row>
                <Col>
                  <InputGroup className="mb-3 w-100">
                    <DropdownButton
                      variant="outline-secondary"
                      title="Card Type"
                      id="input-group-dropdown-1"
                    >
                      <Dropdown.Item name="cardType" onClick={changeHandler}>
                        Visa
                      </Dropdown.Item>
                      <Dropdown.Item name="cardType" onClick={changeHandler}>
                        MasterCard
                      </Dropdown.Item>
                      <Dropdown.Item name="cardType" onClick={changeHandler}>
                        Maestro
                      </Dropdown.Item>
                      <Dropdown.Item name="cardType" onClick={changeHandler}>
                        RuPay
                      </Dropdown.Item>
                    </DropdownButton>
                  </InputGroup>
                </Col>
                <Col className="w-50">
                  <DropdownButton
                    className="w-50 justify-content-center"
                    variant="outline-secondary"
                    title="Bank"
                    id="input-group-dropdown-1"
                  >
                    <Dropdown.Item name="cardType" onClick={changeHandler}>
                      ICICI Bank
                    </Dropdown.Item>
                    <Dropdown.Item name="cardType" onClick={changeHandler}>
                      Axis Bank
                    </Dropdown.Item>
                    <Dropdown.Item name="cardType" onClick={changeHandler}>
                      State Bank of India
                    </Dropdown.Item>
                    <Dropdown.Item name="cardType" onClick={changeHandler}>
                      HDFC Bank
                    </Dropdown.Item>
                    <Dropdown.Item name="cardType" onClick={changeHandler}>
                      Standard Chartered Bank
                    </Dropdown.Item>
                  </DropdownButton>
                </Col>
              </Row>
              <Row className="my-2 align-items-center">
                <Col>Expiry</Col>
                <Col>
                  <Input placeholder="MM/YY" />
                </Col>
                <Col>CVV</Col>
                <Col>
                  <Input placeholder="***" />
                </Col>
              </Row>
            </CardBody>
          </Card>
        </Container>
      </div>
      <div className="Checkout__right">
        <h4>Total</h4>
        <Container className="d-flex flex-column ">
          <Row>
            <Col>Items: </Col>
            <Col>{cartDetails.length}</Col>
          </Row>
          <Row>
            <Col>SubTotal: </Col>
            <Col>{subtotal}</Col>
          </Row>
          <Row>
            <Col>Delivery Charges: </Col>
            <Col>{deliveryCharges}</Col>
          </Row>
          <Row className="Cart__right__total align-self-center mt-2">
            <Col>Total: </Col>
            <Col>{subtotal + deliveryCharges}</Col>
          </Row>
          <Row className="w-50 align-self-center my-3">
            <Button
              variant="warning"
              onClick={() => {
                navigate("/Checkout");
              }}
            >
              Buy
            </Button>
          </Row>
          <Row className="w-75 align-self-center ">
            <Button
              variant="light"
              onClick={() => {
                navigate("/Cart");
              }}
            >
              Go back to Cart
            </Button>
          </Row>
        </Container>
      </div>
    </div>
  );
}

export default Checkout;
