  const [regBuyer, setRegBuyer] = useState({
    FirstName: "",
    LastName: "",
    EmailId: "",
    Password: "",
    ConfirmPassword: "",
    Country: "",
    MobileNo: "",
  });

  //   const navigate = useNavigate;

