//////////////////////////////////////////////////////////////////////
// Purpose: Product Page Component
// Author: Siddharth Karanchery
//////////////////////////////////////////////////////////////////////

import React, { useEffect, useState } from "react";

import ImageCarousel from "../ImageCarousel/ImageCarousel";

import {
  FaShareAlt,
  FaTwitter,
  FaFacebook,
  FaLinkedin,
  FaHeart,
} from "react-icons/fa";

import {
  Container,
  Row,
  Col,
  Accordion,
  Button,
  Modal,
  Card,
  CardBody,
  Form,
  InputGroup,
  Input,
  CardFooter,
} from "react-bootstrap";

import { useParams, useNavigate } from "react-router-dom";

import productDetails from "../../Data/ProductDetails.json";

import "./ProductPage.css";

function ProductPage(props) {
  const [showModal, setShowModal] = useState(false);
  const [mail, setMail] = useState({
    FromName: "",
    FromEmail: "",
    ToName: "",
    ToEmail: "",
    URL: "",
  });

  const { product } = useParams();
  const navigate = useNavigate();

  const onChangeHandler = (e) => {
    e.persist();

    setMail({ ...mail, [e.target.name]: e.target.value });
  };

  const onSubmitHandler = (e) => {
    e.preventDefault();
    if (
      mail.Name.length === 0 ||
      mail.Email.length === 0 ||
      mail.Subject.length === 0 ||
      mail.Message.length === 0
    ) {
      alert("Please fill all the details before submitting.");
      return;
    }

    const data = {
      Name: mail.Name,
      Email: mail.Email,
      Phone: mail.Phone,
      Subject: mail.Subject,
      Message: mail.Message,
    };

    emailjs.init("user_dnwbctoN27KW7QfBkRJHH");

    // console.log("Data:", data);

    emailjs
      .send(
        "service_3e6grlj",
        "template_iz2trua",
        data,
        "user_dnwbctoN27KW7QfBkRJHH"
      )
      .then(
        function () {
          alert("Thank you for contacting BetterMart.com.");
          //   console.log("SUCCESS!");
        },
        function (error) {
          alert("Oops!Something went wrong.", error);
          //   console.log("FAILED...", error);
        }
      );

    setMail({
      Name: "",
      Email: "",
      Subject: "",
      Message: "",
    });
  };

  const shareModal = (
    <Modal
      show={showModal}
      onHide={() => {
        setShowModal(false);
      }}
      keyboard={true}
    >
      <Modal.Header closeButton>
        <Modal.Title>Share </Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <Container className="w-100">
          <Row className="justify-content-center">
            <Col fluid="true">
              <Card className="mx-4">
                <CardBody className="p-4">
                  <Form onSubmit={onSubmitHandler}>
                    <InputGroup className="mb-3">
                      <Input
                        type="text"
                        name="Name"
                        id="Name"
                        placeholder="Name"
                        value={mail.Name}
                        onChange={onChangeHandler}
                      />
                    </InputGroup>

                    <InputGroup className="mb-3">
                      <Input
                        type="email"
                        placeholder="Email"
                        name="Email"
                        id="Email"
                        value={mail.Email}
                        onChange={onChangeHandler}
                      />
                    </InputGroup>

                    <InputGroup className="mb-3">
                      <Input
                        type="text"
                        placeholder="Subject"
                        name="Subject"
                        id="Subject"
                        value={mail.Subject}
                        onChange={onChangeHandler}
                      />
                    </InputGroup>

                    <InputGroup className="mb-4">
                      <Input
                        type="textarea"
                        placeholder="Message"
                        name="Message"
                        id="Message"
                        value={mail.Message}
                        onChange={onChangeHandler}
                      />
                    </InputGroup>

                    <CardFooter className="p-4 ">
                      <Row>
                        {/* <Col xs="12" sm="6"> */}
                        <Button
                          type="submit"
                          className="btn btn-success mb-1"
                          block
                        >
                          <span>Send</span>
                        </Button>
                      </Row>
                    </CardFooter>
                  </Form>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </Modal.Body>

      <Modal.Footer>
        <Button variant="primary">Send</Button>
      </Modal.Footer>
    </Modal>
  );
  return (
    <div className="ProductPage">
      {showModal ? shareModal : null}
      <div className="ProductPage__ele">
        <ImageCarousel images={productDetails.pictures} />
      </div>
      <div className="ProductPage__ele" id="ele__details">
        <Container class="d-flex align-items-center">
          <Row>
            <h2>{product}</h2>
          </Row>
          <Row>
            <h4>Description</h4>
            <p>{productDetails.description}</p>
          </Row>
          <Row>
            <Col>
              <h4>Price</h4>
            </Col>
            <Col>
              <h4>
                {productDetails.currency} {productDetails.price}
              </h4>
            </Col>
          </Row>
          <Row>
            <Col>
              <p>Shipping Charges</p>
            </Col>
            <Col>
              <p>
                {productDetails.currency} {productDetails.shipping}
              </p>
            </Col>
          </Row>
          <Row>
            <Col>
              <Button variant="success">Add to Cart</Button>
            </Col>
            <Col>
              <Button
                variant="warning"
                onClick={() => {
                  navigate("/Cart");
                }}
              >
                Go to Cart
              </Button>
            </Col>
            <Col>
              <Button variant="secondary">Enquiry</Button>
            </Col>
          </Row>
          <Row className="ProductPage__ele__social">
            <Col>
              <FaHeart
                onClick={() => {
                  setShowModal(true);
                }}
              />
            </Col>
            <Col>
              <FaShareAlt />
            </Col>
            <Col>
              <FaTwitter
                className="Header__header__nav__icons"
                size={20}
                onClick={() => {
                  window.open("https://www.twitter.com", "_blank");
                }}
              />
            </Col>
            <Col>
              <FaFacebook
                className="Header__header__nav__icons"
                size={20}
                onClick={() => {
                  window.open("https://www.facebook.com", "_blank");
                }}
              />
            </Col>
            <Col>
              <FaLinkedin
                className="Header__header__nav__icons"
                size={20}
                onClick={() => {
                  window.open("https://www.linkedin.com", "_blank");
                }}
              />
            </Col>
          </Row>
          <Row>
            <h4>Product Details</h4>
            <Accordion defaultActiveKey="" flush>
              <Accordion.Item eventKey="0">
                <Accordion.Header>Brand</Accordion.Header>
                <Accordion.Body>{productDetails.brand}</Accordion.Body>
              </Accordion.Item>
              <Accordion.Item eventKey="1">
                <Accordion.Header>Type</Accordion.Header>
                <Accordion.Body>{productDetails.type}</Accordion.Body>
              </Accordion.Item>
              <Accordion.Item eventKey="2">
                <Accordion.Header>Origin</Accordion.Header>
                <Accordion.Body>{productDetails.origin}</Accordion.Body>
              </Accordion.Item>
            </Accordion>
          </Row>
        </Container>
      </div>
    </div>
  );
}

export default ProductPage;
