//////////////////////////////////////////////////////////////////////
// Purpose: Cart Component
// Author: Siddharth Karanchery
//////////////////////////////////////////////////////////////////////

import React, { useEffect, useState } from "react";

import ImageCarousel from "../ImageCarousel/ImageCarousel";

import { FaShareAlt, FaTwitter, FaFacebook, FaLinkedin } from "react-icons/fa";

import { Container, Row, Col, Accordion, Button, Image } from "react-bootstrap";
import { Input } from "reactstrap";

import { useParams } from "react-router-dom";

import cartDetails from "../../Data/CartDetails.json";

import "./Cart.css";

// - Product thumbnail
// - Brief info
// - Color, size selected
// -  Add/Update carts in real time
// -  Shipping options
// - Order sub total, calculated value
// - Continue shopping button (will take back user to the products section)
// - Checkout button (will move forward user to the checkout screen)

function CartCard(props) {
  return (
    <Row className="border-bottom my-2 w-100 mx-3 ">
      <Col>
        <Image className="w-50 h-50" src={props.item.pictures[0].url} alt="" />
      </Col>
      <Col>{props.item.brand + ", " + props.item.name}</Col>
      <Col>
        <Input type="text" className="w-25" value={props.item.quantity} />
      </Col>
      <Col>{props.item.shippingOptions}</Col>
    </Row>
  );
}

export default CartCard;
