//////////////////////////////////////////////////////////////////////
// Purpose: Cart Card Component
// Author: Siddharth Karanchery
//////////////////////////////////////////////////////////////////////

import React, { useEffect, useState } from "react";

import { MdOutlineDelete } from "react-icons/md";

import { Row, Col, Image } from "react-bootstrap";

import "./Cart.css";

// - Product thumbnail
// - Brief info
// - Color, size selected
// -  Add/Update carts in real time
// -  Shipping options
// - Order sub total, calculated value
// - Continue shopping button (will take back user to the products section)
// - Checkout button (will move forward user to the checkout screen)

function CartCard(props) {
  return (
    <Row className="border-bottom my-2 w-100 mx-3 d-flex align-items-center justify-content-around">
      <Col>
        <Image className="w-50 h-70" src={props.item.productImage} alt="" />
      </Col>
      <Col className="Cart__card__name">{props.item.productName}</Col>
      <Col>
        <input
          type="text"
          className="Cart__card__input"
          value={props.item.productQuantity}
        />
      </Col>
      <Col>{"Price: Rs. " + props.item.productPrice}</Col>
      <Col>{"Delivery Charges: Rs. " + props.item.deliveryCharges}</Col>
      <Col>
        <MdOutlineDelete size={25} />
      </Col>
    </Row>
  );
}

export default CartCard;
